import styled from 'styled-components'
import {Link as LinkR } from 'react-router-dom' // 일반 링크 (다른페이지)
import {Link as LinkS } from 'react-scroll' // 반응스크롤 npm install react-scroll

export const Nav = styled.nav`
    background: #000;
    height: 80px;
    //margin-top: -80px;
