    align-left : 24px;
    font-weight: bold;
    text-decoration: none;
`;


export const MobileIcon = styled.div`
    display:none; /* 평소에 표시되지않음 */

    @media screen and (max-width: 768px) {
        display: block;
        position: absoulute;
        top: 0;
        right: 0;
        transform: translate(-100%, 60%);
        font-size: 1.8rem;
        curosr: pointer;

    }
`
 
export const NavMenu = styled.div`
    display: flex;
    align-items: center;
    list-style: none;
    text-align: bottom;
    margin-right: -220px;

    @media screen and (max-width: 768px) {
        display:none;
    }
`
export const NavItem=styled.li`
    height: 80px;
`

// 반응 스크롤 : s연결하면 맨위로이동
export const NavLinks = styled(LinkS)`
    color:#fff;
    display: flex;
    aling-items: center;
    text-decoration: none;
    padding: 0 1rem;
    height: 100%;
    cursor:pointer;

    &.active {
        border-bottom: 3px solid #01bf71;
    }
`
